import React from 'react'
import { Link, withRouter } from 'react-router-dom'
import { useEffect, useState } from 'react';
import jwt_decode from 'jwt-decode';

import '../../App.css'
import BackgroundImage from '../../assets/images/bg.png'

//export default function LandingPage() {
class LandingPage extends React.Component {

    constructor(props){
        // const [user, setUser] = useState({});
        super(props);
            this.state={
                user:{}, 
                setUser:{}
            }

    }

    componentDidMount(){
        // useEffect = (() => {
            /* global google */
            google.accounts.id.initialize({
              client_id: "787802789658-som6i2h91g2t708bhkg5m3aen4tb1rpa.apps.googleusercontent.com",
              callback: this.handleCallbackResponse()
            })

            google.accounts.id.renderButton(
              document.getElementById("signInDiv"),
              { theme:"outline", size: "large" }
            )

            google.accounts.id.prompt()
        //   }, []);
    }


   handleCallbackResponse = (response) => {
    const { user, setUser} = this.state;
    console.log("Encoded JWT ID token: " + response.credential);
    var userObject = jwt_decode(response.credential);
    console.log(userObject);
    // this.setUser(userObject);
    this.setState({setUser:userObject});
    window.location.replace("localhost:3000/home");
    document.getElementById("signInDiv").hidden = false;
  }

  // prompt again
  handleSignOut = (event) => {
    const { user, setUser} = this.state;
    // this.setUser({});
    this.setState({setUser:{}});
    document.getElementById("signInDiv").hidden = false;
    google.accounts.id.prompt()
  }

//   useEffect = (() => {
//     /* global google */
//     google.accounts.id.initialize({
//       client_id: "787802789658-som6i2h91g2t708bhkg5m3aen4tb1rpa.apps.googleusercontent.com",
//       callback: this.handleCallbackResponse()
//     })

//     google.accounts.id.renderButton(
//       document.getElementById("signInDiv"),
//       { theme:"outline", size: "large" }
//     )

//     google.accounts.id.prompt()
//   }, []);


  render() {
    const { user, setUser} = this.state;
    return (
            <div className="App">
                {/* {()=>this.useEffect()} */}
                    <div id="signInDiv"></div>
                    {
                        Object.keys(user).length != 0 &&
                        <button onClick= { (e) => this.handleSignOut(e)}>Sign Out</button>
                    }
                    { user && 
                        <div>
                            <img src={user.picture}></img>
                            <h3>{user.name}</h3>
                        </div>
                    }

            { <header style={ HeaderStyle }>
                <h1 className="main-title text-center">login / register page</h1>
                <p className="main-para text-center">join us now and don't waste time</p>
                <div className="buttons text-center">
                    <Link to="/login">
                        <button className="primary-button">log in</button>
                    </Link>
                    <Link to="/register">
                        <button className="primary-button" id="reg_btn"><span>register </span></button>
                    </Link>
                </div>
            </header>
            }   
        </div>

    );
  }
}

export default withRouter(LandingPage);

const HeaderStyle = {
    width: "100%",
    height: "100vh",
    background: `url(${BackgroundImage})`,
    backgroundPosition: "center",
    backgroundRepeat: "no-repeat",
    backgroundSize: "cover"
}
